import React from 'react';
import classes from './Condtions.module.css'
import { Sunny, Cloudy, Rain, Snow } from 'weather-styled-icon'; 

function Icon(props){
    if(props.id === 803){
        return <Cloudy />;
    }
    return null;
}

const Conditions = (props) => {
    return (
        <div className={classes.Wrapper}>
            {props.error && <small className={classes.Small}>Please enter a valid city.</small>}

            {props.loading && <div className={classes.Loader} />}

            {props.responseObj.cod === 200 ?
                <div>
                    <p>In <strong>{props.responseObj.name}, {props.responseObj.sys.country}</strong> it is currently {Math.round(props.responseObj.main.temp)} degrees outside with {props.responseObj.weather[0].description}.</p>
                    <Icon id = {props.responseObj.weather[0].id} />;
                </div>
                
            : null
            }
        </div>
    )
 }

export default Conditions;
